import { takeEvery, put, all, call } from "redux-saga/effects";
import UserActionTypes from "./user.types";
import { googleSignInSuccess, googleSignInFailure } from "./user.actions";

import {
  auth,
  googleProvider,
  createUserProfileDocument,
} from "../../firebase/firebase.utils";

export function* googleSignIn() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider); // отримуємо юзера з фаєрбейзового метода
    const userRef = yield call(createUserProfileDocument, user); // створюємо юзер-референс з допомогою функції, яку ми створили в утілітах фаєрбезу
    const userSnapshot = yield userRef.get(); // беремо снепшот юзера з цього юзер-референса
    yield put(
      googleSignInSuccess({ id: userSnapshot.id, ...userSnapshot.data() }) // поміщаємо айді та інші дані юзера в редюсер. з допомогою екшена
    );
  } catch (error) {
    yield put(googleSignInFailure({ error: error })); // виводимо і поміщаємо в редюсер помилку
  }
}

export function* googleSignInStart() {
  yield takeEvery(UserActionTypes.GOOGLE_SIGN_IN_START, googleSignIn);
}

export default function* userSagas() {
  yield all([call(googleSignInStart)]);
}
